classdef HydrophoneFunctions < TransmAndReflCoeff
    
    properties (Access=public)
        r;
        a;
        z;
    end
    
    methods
        function obj = HydrophoneFunctions(a,r,z,f)   % contructor
            obj@TransmAndReflCoeff(f); % invoking the superclass constructor
            obj.r = r;
            obj.z = z;
            obj.a = a;
        end
        function g_fun = g_FilonEchoAll(obj,eta)
            z_fluid = obj.z1*2-obj.z;
            h_fz = (-i.*(-( obj.h_f.^2-eta.^2)) .^(1/2));
            g_fun = -h_fz.*(-z_fluid);
        end
        function g_fun = g_FilonTransmAll(obj,eta)
            z_fluid = obj.z-obj.d;
            h_fz = (-i.*(-( obj.h_f.^2-eta.^2)) .^(1/2));
            g_fun = -h_fz.*(-z_fluid);
        end
        function g_fun = g_FilonFreeField(obj,eta)
            z_fluid = obj.z;
            h_fz = (-i.*(-( obj.h_f.^2-eta.^2)) .^(1/2));
            g_fun = -h_fz.*(-z_fluid);
        end
        function f_fun = f_FilonEcho(obj,eta)
            a = obj.a;
            r = obj.r;
            h_fz = (-i.*(-( obj.h_f.^2-eta.^2)) .^(1/2));
            R = obj.ReflectionCoeff(eta);
            f_fun = (R.*besselj(1,a.*eta).*besselj(0,eta.*r))./h_fz;
        end
